import React from 'react'
import {connect} from 'react-redux'
import {deselect} from 'actions'

const Description = ({selectTr, deselect, scroll}) => {
	return (
		<ul className={scroll > 200 ? 'description list-inline d-flex flex-wrap justify-content-center top' : 
									 'description list-inline d-flex flex-wrap justify-content-center'}>  
			<li>Выбран пользователь: <b>{selectTr.firstName + ' ' + selectTr.lastName}</b></li> 
			<li>Адрес проживания: <b>{selectTr.address.streetAddress}</b></li>
			<li>Город: <b>{selectTr.address.city}</b></li>
			<li>Провинция/штат: <b>{selectTr.address.state}</b></li>
			<li>Индекс: <b>{selectTr.address.zip}</b></li>
			<li><button className='btn btn-danger' onClick={deselect}>Закрыть</button></li>
	 	</ul>
	)
}

const mapStateToProps = state => ({
	selectTr: state.tablePage.select,
	scroll: state.tablePage.scroll,
})

const mapDispatchToProps = {
	deselect,
}

export default connect(mapStateToProps, mapDispatchToProps)(Description)