import React from 'react'
import {connect} from 'react-redux'
import {fetchTable, fetchBigTable, testError} from 'actions'

const TestButtons = ({fetchBigTable, fetchTable, testError}) => {
	return (
		<div className='row justify-content-center'>
			<button className='btn btn-warning col-11 col-sm-6 col-md-4 col-lg-3' 
					onClick={fetchBigTable}
					>
					Загрузить много данных
			</button>
			<button className='btn btn-info col-11 col-sm-6 col-md-4 col-lg-3' 
					onClick={fetchTable}
					>
					Загрузить мало данных
			</button>
			<button className='btn btn-danger col-11 col-sm-6 col-md-4 col-lg-3' 
					onClick={testError}
					>
					Проверить обработку ошибки
			</button>
		</div>		
	)
}

const mapDispatchToProps = {
	fetchTable,
	fetchBigTable,
	testError
}

export default connect(null, mapDispatchToProps)(TestButtons)