import R from 'ramda'

const initialState = {}

export default (state = initialState, {type, payload}) => {
	switch (type) {
		case 'FETCH_TABLE_SUCCESS':
      		const newValues = R.indexBy(R.prop('id'), payload)
      		// если не надо перезаписывать старые данные
      		// return R.merge(state, newValues) 
      		return newValues
  		default:
  			return state
	}
}